import React, {useState} from 'react';



const AddData = props => {

    const grades = [
        { label: "O", value: 10 },
        { label: "A+", value: 9 },
        { label: "A", value: 8 },
        { label: "B+", value: 7 },
        { label: "B", value: 6 },
        { label: "RA", value: 0}
    ];

    const credits = [
        { label: "10", value: 10 },
        { label: "4", value: 4 },
        { label: "3", value: 3 },
        { label: "2", value: 2 }
    ]


    const [subjectName, setSubjectName] = useState("");
    const [grade, setGrade] = useState("");
    const [credit, setCredit] = useState("");

    const gradeChangeHandler = (event) => {
        setGrade(event.target.value);
        console.log(event.target.value);
      };

      const subjectNameHandler = (event) => {
          setSubjectName(event.target.value);
          console.log(event.target.value)
      };

      const addDataHandler = props => {

      };

    

  return (
    <form onSubmit={addDataHandler}>
        <label htmlFor="subname">Subject Name</label>
        <input id="subname" value={subjectName} type="text" onChange={subjectNameHandler}/>

        <label htmlFor="grade">Grade</label>
        <select id="grade" onChange={gradeChangeHandler}> 
      <option value={grade}>  Select Grade  </option>
            
         {grades.map((grade) => <option key={grade.label} value={grade.value}>{grade.label}</option>)}
    </select>

    <label htmlFor="credit">Grade</label>
        <select id="credit"> 
      <option value={credit}>  Select Credit  </option>
            
         {credits.map((credit) => <option key={credit.label} value={credit.value}>{credit.label}</option>)}
    </select>
        
    <button type="submit">Add Data</button> 
    <button>Get GPA</button>
        
        
    </form>
  );
};

export default AddData;
