import React, {useState} from 'react';



const AddData = props => {

    let grades = [
        { label: "O", value: 10 },
        { label: "A+", value: 9 },
        { label: "A", value: 8 },
        { label: "B+", value: 7 },
        { label: "B", value: 6 },
        { label: "RA", value: 0}
    ];

    let credits = [
        { label: "10", value: 10 },
        { label: "4", value: 4 },
        { label: "3", value: 3 },
        { label: "B+", value: 2 }
    ]



    let [grade, setGrade] = useState("Select Grade");

    let gradeChangeHandler = (e) => {
        setGrade(e.target.value);
        console.log(e.target.value);
      }

    

  return (
    <form>
        <label htmlFor="subname">Subject Name</label>
        <input id="subname" type="text" />

        <label htmlFor="grade">Grade</label>
        <select id="grade" onChange={gradeChangeHandler}> 
      <option value="Select grade">  Select Grade  </option>
            {/* Mapping through each fruit object in our fruits array
          and returning an option element with the appropriate attributes / values.
         */}
         {grades.map((grade) => <option key={grade.label} value={grade.value}>{grade.label}</option>)}
    </select>
        
        
        
        
    </form>
  );
};

export default AddData;
